mapper.select:
SELECT a.*,(select a1.roleName from sysinforole a1 WHERE a1.roleId = a.roleId) roleName FROM sysinfouser a WHERE 1=1
AND (a.userName LIKE '%${keyWord}%' or a.userId LIKE '%${keyWord}%')
ORDER BY a.createTime DESC LIMIT #{start}, #{pageSize}

mapper.update:
UPDATE sysinfouser
   SET isUsed = #{isUsed} 
 WHERE userId = #{userId}

UPDATE sysinfouser
   SET passWord = #{passWord} 
 WHERE userId = #{userId}

mapper.select:
SELECT a.userName
  FROM sysinfouser a 
 WHERE a.roleId=#{roleId}
